class Vector2D:
    def __init__(self, x=0, y=0):
        self.x = x
        self.y = y

    def add(self, v):
        return Vector2D(self.x + v.x, self.y + v.y)

    def subtract(self, v):
        return Vector2D(self.x - v.x, self.y - v.y)

    def multiply(self, scalar):
        return Vector2D(self.x * scalar, self.y * scalar)
